INSERT INTO `kick_content` VALUES (40, 129, 'Uyuni Salt Flats ', 0x7579756e692d666c617473, '<p>\r\n	<span style="font-size:14px;"><span style="color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">The great Salt&nbsp;</span><span style="font-weight: bold; color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">Flats</span><span style="color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">&nbsp;of&nbsp;</span><span style="font-weight: bold; color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">Uyuni</span><span style="color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">, is the world''s largest salt&nbsp;</span><span style="font-weight: bold; color: rgb(68, 68, 68); font-family: arial, sans-serif; line-height: 16px;">flat&nbsp;</span></span><span style="color: rgb(17, 17, 17); font-family: Arial, ''Helvetica Neue'', Helvetica, sans-serif; font-size: 14px; line-height: 21.984375px;">at nearly 11,000 square km (4000 acres) of salt plains that seem to stretch out endlessly in every direction. Perhaps one of the most breathtaking landscapes in all of South America, the Uyuni Salt Flats make up a beautiful white desert that begs to be explored.</span></p>\r\n<p>\r\n	<a href="files/galapagos_archipelago.pdf">Download NOW</a></p>\r\n<p>\r\n</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', 1, 21, '2013-11-25 11:39:20', 845, '', '2014-01-18 13:00:44', 845, 845, '2014-01-18 13:13:51', '2013-11-25 11:39:20', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_p