INSERT INTO `kick_content` VALUES (42, 132, 'Familiriazation Trip', 0x66616d2d74726970, '<p>\r\n	<span style="font-size:12px;"><span style="font-family:arial,helvetica,sans-serif;"><span class="sitetext" style="vertical-align: baseline; outline: 0px; padding: 0px; margin: 0px; border: 0px; line-height: 18px;"><span style="line-height: normal;">The Okanagan Valley is BCs premier grape growing region.&nbsp;</span><span style="line-height: normal;">From world-class operations to family-run boutique vineyards, Okanagan wineries are rich with character and consistently ranked among the worlds best at International competitions. Our wineries familiarization tour will</span>&nbsp;give you &nbsp;a genuine feel for the place and culture as well as a first hand look at the region''s wine making industry.</span></span></span></p>\r\n', '', 1, 22, '2013-11-26 13:45:41', 844, '', '2014-02-10 04:26:53', 845, 0, '0000-00-00 00:00:00', '2013-11-26 13:45:41', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 7, 3, '', '', 1, 125, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `kick_content` VALUES (43, 133, 'Overseas studies', 0x6f766572736561732d73747564696573, '<div>\r\n	Canada has been ranked by the United Nations as the number one country in the world in which to live. The English spoken here is proper, standard and easily understood. Vancouver is a recreational paradise as there are endless opportunities for exploring the natural beauty and culture of the city, which is nestled between the Pacific Ocean and lush, coastal mountains. The mild climate of Vancouver makes many activities possible year-round.&nbsp;This is an ideal combination to make Vancouver, Canada your gateway to higher eduation whether it be&nbsp;college or university in Canada or the United States.</div>\r\n<div>\r\n	&nbsp;</div>\r\n', '', 1, 22, '2013-11-26 13:46:54', 844, '', '2013-11-27 15:34:49', 845, 845, '2013-11-27 15:34:49', '2013-11-26 13:46:54', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 2, '', '', 1, 81, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `kick_content` VALUES (44, 134, 'Summer Camps', 0x73756d6d65722d63616d7073, '<p>\r\n	<span style="font-family:times new roman,times,serif;">At Elite Globe Travel, we believe in the balanced growth of the whole person thus we provide Summer Camps that not only &nbsp;expose participants to language learning but also to new activities, physical recreation and the arts. Through this valuable expereince students will develop their own unique personalities, attitude, and &nbsp;identity. They will also have a wonderful opportunity to develop new friendships, become more independent and most of all have an understanding and appreaciation for people of other countries and cultures. </span></p>\r\n<p>\r\n	<span style="font-family:times new roman,times,serif;">* Researchers say, &nbsp;"many of the positive skills children lear at summer camp are called upon later in life"</span></p>\r\n', '', 1, 22, '2013-11-26 13:47:35', 844, '', '2014-01-21 13:50:08', 845, 0, '0000-00-00 00:00:00', '2013-11-26 13:47:35', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 3, 1, '', '', 1, 67, '{"robots":"","author":"","rights"