INSERT INTO `kick_content` VALUES (48, 138, 'Camp Boracay', 0x63616d702d626f7261636179, '<p style="margin-top:0in">\r\n	<span style="font-size: 9pt; font-family: Arial, sans-serif; color: black; background-position: initial initial; background-repeat: initial initial;">Camp Boracay is one of the most beautiful and unique places to learn and live. You will take part in outdoor activities that will let you practice your English with the local people and experience the wonders of Boracay and the Philippines through guided social activities. This most popular camp consists of themed English learning in the mornings with afternoon activities. Camp Boracay is the ideal program for parents wanting to give their young children the experience of both travel and education in one of the most beautiful islands in the South Pacific.</span><span style="font-size:9.0pt;\r\nfont-family:&quot;Arial&quot;,&quot;sans-serif&quot;;color:black"><o:p></o:p></span></p>\r\n', '', 1, 19, '2014-01-18 14:07:04', 845, '', '2014-02-10 05:13:11', 845, 845, '2014-02-10 05:13:11', '2014-01-18 14:07:04', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_fronte