INSERT INTO `kick_content` VALUES (49, 139, 'STUDENTS', 0x73747564656e7473, '<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(255, 0, 0);">SAFETY &amp; SECURITY</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">At Elite Globe Travel, we take the safety of each and every participant on our tours very seriously. Just as we work tirelessly to provide once-in-a-lifetime experiences to group leaders and students, we are equally committed to ensuring that all of our travelers return home safely.&nbsp;</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(255, 0, 0);">GETTING READY TO TRAVEL</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;"><span style="color: rgb(51, 51, 51); line-height: 18pt;">Expect fun, expect adventure, expect to return home with stories youll be telling family and friends for years. You can also expect the following.</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-size:14px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Who Travels With You?&nbsp;</strong></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong style="font-family: ''lucida sans unicode'', ''lucida grande'', sans-serif;"><span style="font-size: 12px;">Your Tour Manager</span></strong></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Tour Manager is an important term in educational travel. It refers to your program leader and provider, who is rigorously trained and specially selected to assist your group while overseas. EGT Tour Managers are humorous, knowledgable in geography, and &nbsp;theyre part commentator, counselor and troubleshooter. &nbsp;He or she supervises hotel check-ins, directs the bus driver and leads educational activities.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<strong><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size:12px;">Your Group Leader</span></span></strong></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Your teacher is your immediate authority on the trip. He or she has taken on a major responsibility for your parents and your school and has devoted time and energy preparing you for the trip. You must realize that everything you do reflects on him or her.&nbsp;</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(255, 0, 0);">PASSPORTS &amp; VISAS</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<strong><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Passports</span></span></strong></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Passports are required &nbsp;for all EGT trips travelling abroad. You are responsible for obtaining&nbsp;<span style="color: rgb(51, 51, 51); line-height: 18pt;">&nbsp;your own passport so please dont delay! Visit your local govenrment office for an application form. Note that some countries require that passports be valid for six months. Please be sure that yours is up-to-date</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<strong><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Visas</span></span></strong></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">EGT will assist you in obtaining visas for the the destination country. Your group leader will collect your group''s passports and send them to the local authority for visa application processing approximately 120 days prior to departure.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><font color="#ff0000">WHAT TO PACK</font></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">As you pack, remember that youll be carrying your suitcase through airports and hotels. If you are purchasing luggage for the trip, consider buying luggage on wheels.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<strong><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Tips:</span></span></strong></p>\r\n<ul style="padding: 0px; margin: 0px 0px 10px 25px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">A good rule of thumb is to lay out everything you think you need, then put half of it back in your closet.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">On your flights and on the bus, you are allowed one suitcase and one carry-on bag. Most coaches have luggage compartments based on the assumption of one suitcase per person, and local safety laws prevent suitcases from being put in the bus aisle. Your carry-on bag needs to be small enough to fit under a bus seat.</span></span></li>\r\n</ul>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><font color="#ff0000">HOW TO BRING IT</font></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Choose a lightweight suitcase, preferably one with wheels. Please be mindful that airlines have specific weight limits and other luggage restrictions. Airlines also require that each article of luggage have at least one tag with your name and address information on it.* Before you leave, make a list of the total contents of your suitcase. If your luggage is lost, the list will help you file any insurance claims.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">*Due to increased airline security, locked luggage is not permitted. When traveling by bus or train, locks may be used.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong><span style="font-size:14px;">Lost or Stolen Luggage</span></strong></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Unless in the care of the airline, your luggage is your responsibility and should never be left unattended. If you lose your luggage on the flight, you must file a claim at the airline''s lost-luggage office before leaving the customs hall of the airport. If your luggage is stolen, you must file a police report to be covered by your comprehensive protection plan.</span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 16px;"><span style="color: rgb(255, 0, 0);">&nbsp;MONEY MATTERS</span></span></span></p>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size:14px;"><strong>Credit Cards &amp; ATM Cards</strong></span></span></p>\r\n<ul style="padding: 0px; margin: 0px 0px 10px 25px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">You may obtain cash with your credit card or local bank card at ATM machines. ATMs generally give a wholesale exchange rate, as much as 5 percent better than banks. You may want to check with your bank to review their fees for withdrawals from international ATMs.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">If you plan on using your bank card to withdraw cash overseas, make sure to contact your bank a few days or weeks before you travel. They can apply a travel approval note on your account that specifies the countries you''re visiting and the dates you''re traveling. This will help prevent your bank from putting a hold on your account for "suspicious activity."&nbsp;</span></span></li>\r\n</ul>\r\n<p style="margin: 0px 0px 15px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><b>Taking Care of Your Valuables</b></span></p>\r\n<ul style="padding: 0px; margin: 0px 0px 10px 25px; color: rgb(88, 88, 88); font-family: edmondsansregular, Geneva, Verdana, Helvetica, Arial, sans-serif; font-size: 14px; line-height: 24px;">\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Your common sense will take you a long way.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Keep your money, passport and other valuables in a hotel safe when available.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">Use a money belt or neck pouch for carrying money. Strap purses and cameras diagonally across, not over, your shoulder and preferably under your clothing.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">It is a good idea to leave expensive cameras, fancy sound equipment and jewelry at home.</span></span></li>\r\n	<li style="line-height: 20px; padding: 0px; margin: 0px;">\r\n		<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="font-size: 12px;">In the event that valuables are lost, you must have a police report to be covered by your comprehensive protection plan.</span></span></li>\r\n</ul>\r\n', '', 1, 2, '2014-01-18 14:30:59', 845, '', '2014-01-20 15:50:08', 845, 0, '0000-00-00 00:00:00', '2014-01-18 14:30:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 5, 7, '', '', 1, 30, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `kick_content` VALUES (50, 140, 'PARENTS', 0x706172656e7473, '<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="color:#ff0000;">SAFETY &amp; SECURITY</span></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">At Elite Globe Travel, we take the safety of each and every participant on our tours very seriously. Just as we work tirelessly to provide once-in-a-lifetime experiences to group leaders and students, we are equally committed to ensuring that all of our travelers return home safely.&nbsp;</span></span></p>\r\n<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="color:#ff0000;">GETTING READY TO TRAVEL</span></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="color: rgb(51, 51, 51); line-height: 18pt;">Expect fun, expect adventure, expect to return home with stories youll be telling family and friends for years. You can also expect the following.</span></span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Who Travels With You?&nbsp;</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><strong style="font-family: ''lucida sans unicode'', ''lucida grande'', sans-serif;">Your Tour Manager</strong></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Tour Manager is an important term in educational travel. It refers to your program leader and provider, who is rigorously trained and specially selected to assist your group while overseas. EGT Tour Managers are humorous, knowledgable in geography, and &nbsp;theyre part commentator, counselor and troubleshooter. &nbsp;He or she supervises hotel check-ins, directs the bus driver and leads educational activities.</span></span></p>\r\n<p>\r\n	<span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Your Group Leader</strong></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Your teacher is your immediate authority on the trip. He or she has taken on a major responsibility for your parents and your school and has devoted time and energy preparing you for the trip. You must realize that everything you do reflects on him or her.&nbsp;</span></span></p>\r\n<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="color:#ff0000;">PASSPORTS &amp; VISAS</span></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Passports</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Passports are required &nbsp;for all EGT trips travelling abroad. You are responsible for obtaining&nbsp;<span style="color: rgb(51, 51, 51); line-height: 18pt;">&nbsp;your own passport so please dont delay! Visit your local govenrment office for an application form. Note that some countries require that passports be valid for six months. Please be sure that yours is up-to-date</span></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Visas</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">EGT will assist you in obtaining visas for the the destination country. Your group leader will collect your group''s passports and send them to the local authority for visa application processing approximately 120 days prior to departure.</span></span></p>\r\n<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><font color="#ff0000">WHAT TO PACK</font></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">As you pack, remember that youll be carrying your suitcase through airports and hotels. If you are purchasing luggage for the trip, consider buying luggage on wheels.</span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Tips:</strong></span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">A good rule of thumb is to lay out everything you think you need, then put half of it back in your closet.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">On your flights and on the bus, you are allowed one suitcase and one carry-on bag. Most coaches have luggage compartments based on the assumption of one suitcase per person, and local safety laws prevent suitcases from being put in the bus aisle. Your carry-on bag needs to be small enough to fit under a bus seat.</span></span></li>\r\n</ul>\r\n<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><font color="#ff0000">HOW TO BRING IT</font></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Choose a lightweight suitcase, preferably one with wheels. Please be mindful that airlines have specific weight limits and other luggage restrictions. Airlines also require that each article of luggage have at least one tag with your name and address information on it.* Before you leave, make a list of the total contents of your suitcase. If your luggage is lost, the list will help you file any insurance claims.</span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">*Due to increased airline security, locked luggage is not permitted. When traveling by bus or train, locks may be used.</span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Lost or Stolen Luggage</strong></span></span></p>\r\n<p>\r\n	<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Unless in the care of the airline, your luggage is your responsibility and should never be left unattended. If you lose your luggage on the flight, you must file a claim at the airline''s lost-luggage office before leaving the customs hall of the airport. If your luggage is stolen, you must file a police report to be covered by your comprehensive protection plan.</span></span></p>\r\n<p>\r\n	<span style="font-size:16px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><span style="color: rgb(255, 0, 0);">&nbsp;MONEY MATTERS</span></span></span></p>\r\n<p>\r\n	<span style="font-size:14px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;"><strong>Credit Cards &amp; ATM Cards</strong></span></span></p>\r\n<ul>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">You may obtain cash with your credit card or local bank card at ATM machines. ATMs generally give a wholesale exchange rate, as much as 5 percent better than banks. You may want to check with your bank to review their fees for withdrawals from international ATMs.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">If you plan on using your bank card to withdraw cash overseas, make sure to contact your bank a few days or weeks before you travel. They can apply a travel approval note on your account that specifies the countries you''re visiting and the dates you''re traveling. This will help prevent your bank from putting a hold on your account for "suspicious activity."&nbsp;</span></span></li>\r\n</ul>\r\n<p>\r\n	<font face="lucida sans unicode, lucida grande, sans-serif"><span style="font-size: 14px;"><b>Taking Care of Your Valuables</b></span></font></p>\r\n<ul>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Your common sense will take you a long way.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Keep your money, passport and other valuables in a hotel safe when available.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">Use a money belt or neck pouch for carrying money. Strap purses and cameras diagonally across, not over, your shoulder and preferably under your clothing.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">It is a good idea to leave expensive cameras, fancy sound equipment and jewelry at home.</span></span></li>\r\n	<li>\r\n		<span style="font-size:12px;"><span style="font-family:lucida sans unicode,lucida grande,sans-serif;">In the event that valuables are lost, you must have a police report to be covered by your comprehensive protection plan.</span></span></li>\r\n</ul>\r\n', '', 1, 2, '2014-01-18 14:31:17', 845, '', '2014-01-20 15:37:18', 845, 0, '0000-00-00 00:00:00', '2014-01-18 14:31:17', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 18, 6, '', '', 1, 51, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `kick_content` VALUES (51, 141, 'About Us', 0x61626f7574, '<p>\r\n	<img alt="" src="images/Gallery/Vancouver/n_banner.jpg" style="width: 1267px; height: 261px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<span style="font-size: 36px;"><strong>Welcome</strong></span>&nbsp;to&nbsp;<span style="color: rgb(255, 0, 0);">Elite Globe Travel!</span>&nbsp;Since our launch in 2012, we have been providing exciting programs, throughout Canada, the United States, Latin America and Asia. Our dedicated team of highly experienced professionals in the industries of travel and education work tirelessly to design unique programs &nbsp;that will leave you with &nbsp;incredible learning experiences and unforgetable memories. Your journey with us will take you to the different cultures of the world where you will meet different people, see their lifestyles and take part in some of the most new unique activities. These are just a few of the many possibilities and there is no denying that our programs will give you so much more. &nbsp;We specialize in family vacations, youth summer and winter camps, special destination getaways and industry familiarization tours. For more information you can contact us at info@eliteglobetravel.com. &nbsp;<span style="font-size: 36px;"><strong>&nbsp;</strong></span></p>\r\n<p>\r\n	<strong>KEEP TRAVELLING, EITHER YOUR BODY OR SOUL WILL BE ON ITS WAY</strong></p>\r\n<p>\r\n	&nbsp;</p>\r\n', '', 1, 2, '2014-01-18 14:35:59', 845, '', '2014-02-10 09:16:54', 845, 0, '0000-00-00 00:00:00', '2014-01-18 14:35:59', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":