INSERT INTO `kick_modules` VALUES (217, 'Scroll Triggered Expanded Module', '', '<p>A feature of Corvus is the <strong>expanded</strong> module. Any module that has the <strong>expanded</strong> module class suffix applied to it, will expand and contract based on window <strong>scroll</strong>. The feature is best applied in the <strong>showcase</strong> position, with a full width module, as demoed currently. The <strong>overlap</strong> effect below, is via CSS and can be customized through the <a href="http://www.gantry-framework.org/documentation/joomla/tutorials/custom_stylesheet.md">custom stylesheet process</a>, depending on your content needs.</p>', 1, 'showcase-a', 0, '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', -2, 'mod_custom', 1, 1, '{"prepare_content":"0","backgroundimage":"","layout":"_:default","moduleclass_sfx":"expanded rt-center","cache":"1","cache_time":"900","cachemode":"static"}', 0, '*');
